export const Logo = ({ width = 60, height = 60 }) => {
  const color = useColorModeValue("black", "white");
  const { colorMode, toggleColorMode } = useColorMode();

  const circlePath =
    "M200 100C200 127.614 177.614 150 150 150C122.386 150 101 127.614 101 100C101 72.3858 122.386 50 150 50C177.614 50 200 72.3858 200 100Z";
  const moonPath =
    "M200 100C200 127.614 177.614 150 150 150C150 150 165.5 127.614 165.5 100C165.5 72.3858 150 50 150 50C177.614 50 200 72.3858 200 100Z";

  const [style, api] = useSpring(() => ({
    shape: circlePath,
    fill: "black",
  }));

  return (
    <svg
      width={width}
      height={height}
      viewBox="0 0 200 200"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
    >
      <path d="M200 0H0V200H200V150H100V50H200V0Z" fill={color} />
      <animated.path
        onClick={toggleColorMode}
        onMouseOver={() =>
          api.start({
            shape: colorMode === "light" ? moonPath : circlePath,
            fill: "#FEDB14",
          })
        }
        onMouseLeave={() => api.start({ shape: circlePath, fill: color })}
        d={style.shape}
        fill={style.fill}
      />
    </svg>
  );
};

